// SPDX-License-Identifier: MIT
pragma solidity >=0.4.22 <0.9.0;

contract RegistroInvestigaciones {

	// Estructura de datos  que contiene la informacion relativa a un registro de una investigación
        struct Investigacion {
        	string nombre_Investigador;
        	string apellido1_Investigador;
        	string apellido2_Investigador;
		string timestamp;
        	string hash_documento;
		uint identificador_documento;
        }

	// Un listado de las investigaciones registradas en la blockchain
        mapping(uint => Investigacion) listaInvestigaciones;

        // Contador del total de cursos disponibles
        uint totalInvestigaciones;

        //  Dirección ETH del administrador (se inicializa en el constructor y se correspondera con la dirección ETH que despliegue el contrato en la red)
        address administrador;

        // Prerequisito para algunas funciones que requieren que sean ejecutadas solo por el administrador
        modifier onlyAdmin {
          require(msg.sender == administrador,
            "Operacion solo permitida para el administrador del contrato."
          );
          _;
        }

        // Prerequisito para algunas funciones que requieren que sean ejecutadas solo por aquellas direcciones ETH autorizadas por el administrador
        modifier onlyAuthorized(address addr) {
          require(addr == 0x00,
            "Usuario no autorizado para realizar dicha operacion."
          );
          _;
        }


        /**
        * Constructor del Smart Contract
        *
        * Inicializa la variable 'totalInvestigaciones' a cero
        */
	constructor() public {
		administrador = msg.sender;
        	totalInvestigaciones = 0;
        }


        /**
        * Recibe como parametros los datos del investigador que registra la investigacion, la marca de tiempo del momento en el que se cifro el fichero y el hash del mismo.
        *
        * @param param_nombre_Investigador --> Nombre del investigador.
        * @param param_apellido1_Investigador --> Primer apellido del investigador.
	* @param param_apellido2_Investigador --> Segundo apellido del investigador.
	* @param param_timestamp --> Marca de tiempo del momento en el que se llevó a cabo el cifrado del fichero.
	* @param param_hash_documento --> Hash del fichero cifrado.
        */
        function registrarInvestigacion(string memory param_nombre_Investigador, string memory param_apellido1_Investigador, string memory param_apellido2_Investigador, string memory param_timestamp, string memory param_hash_documento) public {

		listaInvestigaciones[totalInvestigaciones].nombre_Investigador = param_nombre_Investigador;
		listaInvestigaciones[totalInvestigaciones].apellido1_Investigador = param_apellido1_Investigador;
		listaInvestigaciones[totalInvestigaciones].apellido2_Investigador = param_apellido2_Investigador;
		listaInvestigaciones[totalInvestigaciones].timestamp = param_timestamp;
		listaInvestigaciones[totalInvestigaciones].hash_documento = param_hash_documento;

             	totalInvestigaciones++;
        }

        /**
        * Esta funcion devuelve el numero total de investigaciones registradas en la blockchain
        *
        * @return _totalInvestigaciones --> el numero total de investigaciones registradas
        */
        function getNumeroTotalInvestigaciones() public view returns(uint _totalInvestigaciones) {
                _totalInvestigaciones = totalInvestigaciones;
        }


        /**
        * Esta función retorna, en base al identificador de la investigacion, los datos relacionados con la misma.
        *
        * @param param_idInvestigacion -> identificador del curso
        *
        * @return _nombre_Investigador --> nombre del investigador
        * @return _apellido1_Investigador --> apellido 1 del investigador
        * @return _apellido2_Investigador --> apellido 2 del investigador
        * @return _timestamp --> timestamp del cifrado del fichero
        * @return _hash_documento --> hash del fichero cifrado
        *
        */
        function getInfoRegistro(uint param_idInvestigacion) public view returns(string memory _nombre_Investigador, string memory _apellido1_Investigador , string memory _apellido2_Investigador, string memory _timestamp, string memory _hash_documento){

		require(param_idInvestigacion >= 0 && param_idInvestigacion < totalInvestigaciones,
		 "Identificador de la investigacion no registrada en la blockchain"
		 );

              	_nombre_Investigador = listaInvestigaciones[param_idInvestigacion].nombre_Investigador;
              	_apellido1_Investigador = listaInvestigaciones[param_idInvestigacion].apellido1_Investigador;
              	_apellido2_Investigador = listaInvestigaciones[param_idInvestigacion].apellido2_Investigador;
              	_timestamp = listaInvestigaciones[param_idInvestigacion].timestamp;
              	_hash_documento = listaInvestigaciones[param_idInvestigacion].hash_documento;

              	return (_nombre_Investigador, _apellido1_Investigador, _apellido2_Investigador, _timestamp, _hash_documento);
        }



        /**
        * Esta función retorna, en base a la direccion que recibe si se trata del administrador del contrato o no.
        *
        * @param addr --> Direccion ETH a comprobar
        */
        function isAdmin(address addr) public view returns(bool resultado) {
                if(addr == administrador) {
                	resultado = true;
                } else {
                	resultado = false;
                }
        }
}

